import create from 'zustand';
import { io } from 'socket.io-client';
import { devtools } from 'zustand/middleware';

const API_URL = process.env.REACT_APP_API_URL || 'http://192.168.175.155:5000';

const socketMiddleware = (config) => (set, get, api) => {
  const initialState = config((...args) => { set(...args); }, get, api);

  let socket = null;
  let reconnectTimer = null;
  const MAX_RECONNECT_ATTEMPTS = 5;
  const BASE_RECONNECT_DELAY = 1000;

  const clearReconnectTimer = () => {
    if (reconnectTimer) {
      clearTimeout(reconnectTimer);
      reconnectTimer = null;
    }
  };

  const createSocketConnection = (cartId) => {
    try {
      // Create new socket connection
      socket = io(API_URL, {
        query: { cart_id: cartId },
        transports: ['websocket'],
        upgrade: false,
        reconnection: true,
        reconnectionAttempts: MAX_RECONNECT_ATTEMPTS,
        reconnectionDelay: BASE_RECONNECT_DELAY,
        reconnectionDelayMax: 5000,
        timeout: 10000,
      });

      // Socket event handlers
      socket.on('connect', () => {
        console.log('Socket connected for cart:', cartId);
        set({ 
          isConnected: true, 
          error: null,
          reconnectAttempts: 0,
          socket, // Store socket instance in state
          cartId
        });
      });

      socket.on('connection_confirmed', (data) => {
        console.log('Connection confirmed:', data);
        set({ 
          maxConnections: data.max_connections,
          reconnectCooldown: data.cooldown * 1000
        });
      });

      socket.on('disconnect', (reason) => {
        console.log('Socket disconnected:', reason);
        set({ isConnected: false });
      });

      socket.on('connect_error', (error) => {
        console.error('Socket connection error:', error);
        set({ error: error.message });
      });

      socket.on('frame_update', (frameData) => {
        if (frameData.cart_id === cartId) {
          set({ 
            lastFrame: frameData.frame,
            error: null
          });
        }
      });

      return socket;
    } catch (error) {
      console.error('Error creating socket connection:', error);
      set({ error: error.message });
      return null;
    }
  };

  return {
    ...initialState,
    initializeSocket: (cartId) => {
      if (!cartId) {
        console.warn('No cart ID provided for socket connection');
        return;
      }

      // Clear any existing reconnect timer
      clearReconnectTimer();

      // Get current socket from state
      const currentSocket = get().socket;

      // If there's an existing connection for this cart, don't create a new one
      if (currentSocket?.connected && get().cartId === cartId) {
        console.log('Socket already connected for cart:', cartId);
        return;
      }

      // Cleanup existing socket if any
      if (currentSocket) {
        currentSocket.disconnect();
      }

      // Reset connection state
      set({ 
        socket: null,
        error: null,
        reconnectAttempts: 0,
        isConnected: false,
        cartId: null,
        lastFrame: null
      });

      // Create new connection
      createSocketConnection(cartId);
    },

    disconnect: () => {
      clearReconnectTimer();
      const currentSocket = get().socket;
      if (currentSocket) {
        currentSocket.disconnect();
      }
      set({ 
        socket: null,
        isConnected: false,
        cartId: null,
        lastFrame: null,
        error: null,
        reconnectAttempts: 0
      });
    },

    cleanup: () => {
      const { disconnect } = get();
      disconnect();
    }
  };
};

const useWebSocketStore = create(
  devtools(
    socketMiddleware((set) => ({
      socket: null,
      isConnected: false,
      cartId: null,
      error: null,
      lastFrame: null,
      reconnectAttempts: 0,
      maxConnections: 1,
      reconnectCooldown: 1000
    }))
  )
);

export default useWebSocketStore;
