import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuthStore } from '../stores/authStore';
import io from 'socket.io-client';
import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function Dashboard() {
  const [detections, setDetections] = useState([]);
  const [cameras, setCameras] = useState([]);
  const [selectedCamera, setSelectedCamera] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  
  const { user } = useAuthStore();
  
  useEffect(() => {
    // Connect to WebSocket
    const socket = io(API_URL);
    
    socket.on('detection', (detection) => {
      setDetections((prev) => [detection, ...prev].slice(0, 10));
    });
    
    // Load cameras
    const loadCameras = async () => {
      try {
        const response = await axios.get(`${API_URL}/admin/cameras`);
        setCameras(response.data);
        if (response.data.length > 0) {
          setSelectedCamera(response.data[0]);
        }
      } catch (error) {
        setError('Failed to load cameras');
      }
    };
    
    // Load recent detections
    const loadDetections = async () => {
      try {
        const response = await axios.get(`${API_URL}/detection/recent`);
        setDetections(response.data);
      } catch (error) {
        setError('Failed to load recent detections');
      }
      setIsLoading(false);
    };
    
    loadCameras();
    loadDetections();
    
    return () => {
      socket.disconnect();
    };
  }, []);
  
  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="text-lg text-gray-600">Loading...</div>
      </div>
    );
  }
  
  return (
    <div className="space-y-6">
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h2 className="text-lg font-medium text-gray-900">Live Camera Feed</h2>
          
          {/* Camera selector */}
          <div className="mt-4 flex space-x-4">
            {cameras.map((camera) => (
              <button
                key={camera.id}
                onClick={() => setSelectedCamera(camera)}
                className={`px-3 py-2 rounded-md text-sm font-medium ${
                  selectedCamera?.id === camera.id
                    ? 'bg-indigo-600 text-white'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                {camera.name}
              </button>
            ))}
          </div>
          
          {/* Camera feed */}
          {selectedCamera ? (
            <div className="mt-4 aspect-w-16 aspect-h-9 bg-black rounded-lg overflow-hidden">
              <img
                src={`${API_URL}/detection/video_feed/${selectedCamera.id}`}
                alt="Live camera feed"
                className="w-full h-full object-cover"
              />
            </div>
          ) : (
            <div className="mt-4 aspect-w-16 aspect-h-9 bg-gray-100 rounded-lg flex items-center justify-center">
              <p className="text-gray-500">No camera selected</p>
            </div>
          )}
        </div>
      </div>
      
      {/* Recent detections */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h2 className="text-lg font-medium text-gray-900">Recent Detections</h2>
          
          <div className="mt-4">
            {detections.length > 0 ? (
              <ul className="divide-y divide-gray-200">
                {detections.map((detection, index) => (
                  <li key={index} className="py-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-gray-900">
                          {detection.product_name}
                        </p>
                        <p className="text-sm text-gray-500">
                          Confidence: {(detection.confidence * 100).toFixed(2)}%
                        </p>
                      </div>
                      <div className="text-sm text-gray-500">
                        {new Date(detection.timestamp).toLocaleTimeString()}
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500">No recent detections</p>
            )}
          </div>
        </div>
      </div>
      
      {/* Quick actions */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h2 className="text-lg font-medium text-gray-900">Quick Actions</h2>
          
          <div className="mt-4 grid grid-cols-1 gap-4 sm:grid-cols-2">
            <Link
              to="/stores"
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700"
            >
              Find Nearby Stores
            </Link>
            
            {user?.role === 'admin' && (
              <Link
                to="/admin"
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-green-600 hover:bg-green-700"
              >
                Admin Dashboard
              </Link>
            )}
          </div>
        </div>
      </div>
      
      {error && (
        <div className="rounded-md bg-red-50 p-4">
          <div className="text-sm text-red-700">{error}</div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
