import React, { useEffect, useRef, useCallback } from 'react';
import { toast } from 'react-hot-toast';
import useCartStore from '../stores/cartStore';
import useWebSocketStore from '../stores/websocketStore';

const CameraFeed = ({ cartId, onProductDetected, onConnectionChange }) => {
  const imageRef = useRef(null);
  const frameBufferRef = useRef('');
  const addItem = useCartStore(state => state.addItem);
  const { 
    initializeSocket, 
    disconnect,
    isConnected,
    error,
    lastFrame
  } = useWebSocketStore();

  const handleProductDetection = useCallback((data) => {
    if (data.class) {
      const product = {
        id: data.class,
        name: data.class,
        price: 0,
        confidence: data.confidence
      };
      addItem(product);
      toast.success(`Detected: ${product.name}`);
    }
  }, [addItem]);

  // Initialize WebSocket connection
  useEffect(() => {
    if (cartId) {
      initializeSocket(cartId);
    }
    return () => {
      disconnect();
    };
  }, [cartId, initializeSocket, disconnect]);

  // Handle connection status changes
  useEffect(() => {
    if (onConnectionChange) {
      onConnectionChange(isConnected);
    }
  }, [isConnected, onConnectionChange]);

  // Handle errors
  useEffect(() => {
    if (error) {
      toast.error(`Connection error: ${error}`);
    }
  }, [error]);

  // Handle frame updates
  useEffect(() => {
    if (!lastFrame) return;

    try {
      // Handle chunked images
      if (lastFrame.chunk) {
        frameBufferRef.current += lastFrame.chunk;
        if (!lastFrame.final) return;
        lastFrame.image = frameBufferRef.current;
        frameBufferRef.current = '';
      }

      if (imageRef.current && lastFrame.image) {
        const img = new Image();
        img.onload = () => {
          imageRef.current.src = img.src;
        };
        img.onerror = () => {
          console.error('Failed to load image');
        };
        img.src = `data:image/jpeg;base64,${lastFrame.image}`;
      }

      // Handle detection if present
      if (lastFrame.class) {
        if (onProductDetected) {
          onProductDetected(lastFrame);
        }
        handleProductDetection(lastFrame);
      }
    } catch (error) {
      console.error('Error processing frame:', error);
    }
  }, [lastFrame, onProductDetected, handleProductDetection]);

  return (
    <div className="bg-white p-4 rounded-lg shadow">
      <div className="mb-4">
        <h2 className="text-xl font-semibold mb-2">Camera Feed</h2>
        <div className="flex items-center space-x-2">
          <div
            className={`w-3 h-3 rounded-full ${
              isConnected ? 'bg-green-500' : 'bg-red-500'
            }`}
          />
          <span className="text-sm text-gray-600">
            {isConnected ? 'Connected' : 'Disconnected'}
          </span>
        </div>
      </div>

      <div className="relative aspect-video bg-gray-100 rounded-lg overflow-hidden mb-4">
        <img
          ref={imageRef}
          alt="Camera feed"
          className="w-full h-full object-cover"
        />
        {!isConnected && (
          <div className="absolute inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50 text-white">
            {error ? 'Connection error' : 'Connecting to camera...'}
          </div>
        )}
      </div>

      {lastFrame?.class && (
        <div className="p-3 bg-blue-50 rounded-lg">
          <h3 className="font-medium mb-1">Last Detection</h3>
          <div className="text-sm text-gray-600">
            <p>Product: {lastFrame.class}</p>
            <p>
              Confidence: {(lastFrame.confidence * 100).toFixed(1)}%
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default CameraFeed;
