import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import io from 'socket.io-client';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function CameraView() {
  const { cameraId } = useParams();
  const [camera, setCamera] = useState(null);
  const [detections, setDetections] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showOverlay, setShowOverlay] = useState(true);
  
  useEffect(() => {
    // Load camera details
    const loadCamera = async () => {
      try {
        const response = await axios.get(`${API_URL}/admin/cameras/${cameraId}`);
        setCamera(response.data);
      } catch (error) {
        setError('Failed to load camera details');
      }
    };
    
    // Load recent detections
    const loadDetections = async () => {
      try {
        const response = await axios.get(`${API_URL}/detection/recent`, {
          params: { camera_id: cameraId }
        });
        setDetections(response.data);
      } catch (error) {
        setError('Failed to load detections');
      }
      setIsLoading(false);
    };
    
    // Connect to WebSocket for real-time updates
    const socket = io(API_URL);
    
    socket.on('detection', (detection) => {
      if (detection.camera_id === parseInt(cameraId)) {
        setDetections((prev) => [detection, ...prev].slice(0, 10));
      }
    });
    
    loadCamera();
    loadDetections();
    
    return () => {
      socket.disconnect();
    };
  }, [cameraId]);
  
  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="text-lg text-gray-600">Loading...</div>
      </div>
    );
  }
  
  return (
    <div className="space-y-6">
      {/* Camera feed */}
      <div className="bg-white shadow rounded-lg overflow-hidden">
        <div className="px-4 py-5 sm:p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-medium text-gray-900">
              {camera?.name || 'Camera Feed'}
            </h2>
            
            <div className="flex items-center space-x-4">
              <button
                onClick={() => setShowOverlay(!showOverlay)}
                className={`px-3 py-1 rounded-md text-sm font-medium ${
                  showOverlay
                    ? 'bg-indigo-600 text-white'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                {showOverlay ? 'Hide Overlay' : 'Show Overlay'}
              </button>
              
              <div className="flex items-center">
                <span
                  className={`h-3 w-3 rounded-full ${
                    camera?.status === 'online' ? 'bg-green-500' : 'bg-red-500'
                  }`}
                />
                <span className="ml-2 text-sm text-gray-500">
                  {camera?.status === 'online' ? 'Online' : 'Offline'}
                </span>
              </div>
            </div>
          </div>
          
          <div className="relative aspect-w-16 aspect-h-9 bg-black rounded-lg overflow-hidden">
            <img
              src={`${API_URL}/detection/video_feed/${cameraId}`}
              alt="Live camera feed"
              className="w-full h-full object-cover"
            />
            
            {showOverlay && detections[0] && (
              <div className="absolute top-4 left-4 bg-black bg-opacity-50 text-white p-3 rounded-lg">
                <p className="text-lg font-medium">
                  {detections[0].product_name}
                </p>
                <p className="text-sm">
                  Confidence: {(detections[0].confidence * 100).toFixed(2)}%
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
      
      {/* Recent detections */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h2 className="text-lg font-medium text-gray-900 mb-4">
            Recent Detections
          </h2>
          
          <div className="flow-root">
            <ul className="-my-5 divide-y divide-gray-200">
              {detections.map((detection, index) => (
                <li key={index} className="py-4">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm font-medium text-gray-900">
                        {detection.product_name}
                      </p>
                      <p className="text-sm text-gray-500">
                        Confidence: {(detection.confidence * 100).toFixed(2)}%
                      </p>
                    </div>
                    <div className="text-sm text-gray-500">
                      {new Date(detection.timestamp).toLocaleTimeString()}
                    </div>
                  </div>
                </li>
              ))}
              
              {detections.length === 0 && (
                <li className="py-4">
                  <p className="text-sm text-gray-500">No recent detections</p>
                </li>
              )}
            </ul>
          </div>
        </div>
      </div>
      
      {error && (
        <div className="rounded-md bg-red-50 p-4">
          <div className="text-sm text-red-700">{error}</div>
        </div>
      )}
    </div>
  );
}

export default CameraView;
