import React from 'react';
import { GoogleMap, useJsApiLoader, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px'
};

const defaultCenter = {
  lat: 20.5937,  // Default center (India)
  lng: 78.9629
};

const MapSelector = ({ onLocationSelect, initialLocation }) => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
  });

  const [marker, setMarker] = React.useState(initialLocation ? {
    lat: Number(initialLocation.lat),
    lng: Number(initialLocation.lng)
  } : null);

  const handleMapClick = (event) => {
    const newLocation = {
      lat: Number(event.latLng.lat()),
      lng: Number(event.latLng.lng())
    };
    setMarker(newLocation);
    onLocationSelect(newLocation);
  };

  if (!isLoaded) return (
    <div className="flex items-center justify-center h-[400px] bg-gray-50">
      <div className="text-gray-500">Loading map...</div>
    </div>
  );

  return (
    <div className="relative">
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={marker || defaultCenter}
        zoom={10}
        onClick={handleMapClick}
        options={{
          fullscreenControl: false,
          streetViewControl: false,
          mapTypeControlOptions: { position: 7 }
        }}
      >
        {marker && (
          <Marker
            position={marker}
            draggable={true}
            onDragEnd={(e) => {
              const newLocation = {
                lat: Number(e.latLng.lat()),
                lng: Number(e.latLng.lng())
              };
              setMarker(newLocation);
              onLocationSelect(newLocation);
            }}
          />
        )}
      </GoogleMap>
      {marker && (
        <div className="absolute bottom-2 left-2 bg-white px-3 py-2 rounded-md shadow-sm text-sm text-gray-600">
          Lat: {marker.lat.toFixed(6)}, Lng: {marker.lng.toFixed(6)}
        </div>
      )}
    </div>
  );
};

export default MapSelector;
