import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Fix for default marker icons in React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

function StoreLocator() {
  const [stores, setStores] = useState([]);
  const [userLocation, setUserLocation] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  
  useEffect(() => {
    // Get user's location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
          
          // Load nearby stores
          loadNearbyStores(position.coords.latitude, position.coords.longitude);
        },
        (error) => {
          console.error('Error getting location:', error);
          // Load all stores if location access is denied
          loadAllStores();
        }
      );
    } else {
      // Load all stores if geolocation is not supported
      loadAllStores();
    }
  }, []);
  
  const loadNearbyStores = async (lat, lng) => {
    try {
      const response = await axios.get(`${API_URL}/store/nearby`, {
        params: {
          latitude: lat,
          longitude: lng,
          radius: 10 // 10km radius
        }
      });
      setStores(response.data);
    } catch (error) {
      setError('Failed to load nearby stores');
    }
    setIsLoading(false);
  };
  
  const loadAllStores = async () => {
    try {
      const response = await axios.get(`${API_URL}/store/search`, {
        params: { q: '' }
      });
      setStores(response.data);
    } catch (error) {
      setError('Failed to load stores');
    }
    setIsLoading(false);
  };
  
  const handleSearch = async (e) => {
    e.preventDefault();
    
    try {
      const response = await axios.get(`${API_URL}/store/search`, {
        params: { q: searchQuery }
      });
      setStores(response.data);
    } catch (error) {
      setError('Failed to search stores');
    }
  };
  
  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="text-lg text-gray-600">Loading...</div>
      </div>
    );
  }
  
  return (
    <div className="space-y-6">
      {/* Search bar */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <form onSubmit={handleSearch} className="flex gap-4">
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search stores by name or address"
              className="flex-1 rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
            />
            <button
              type="submit"
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700"
            >
              Search
            </button>
          </form>
        </div>
      </div>
      
      {/* Map */}
      <div className="bg-white shadow rounded-lg overflow-hidden">
        <div className="h-[600px]">
          <MapContainer
            center={userLocation || [0, 0]}
            zoom={userLocation ? 13 : 2}
            style={{ height: '100%', width: '100%' }}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            
            {userLocation && (
              <Marker position={[userLocation.lat, userLocation.lng]}>
                <Popup>Your location</Popup>
              </Marker>
            )}
            
            {stores.map((store) => (
              <Marker
                key={store.id}
                position={[store.latitude, store.longitude]}
              >
                <Popup>
                  <div>
                    <h3 className="font-medium">{store.name}</h3>
                    <p className="text-sm text-gray-500">{store.address}</p>
                    <p className="text-sm text-gray-500">
                      {store.camera_count} camera{store.camera_count !== 1 && 's'}
                    </p>
                    {store.distance && (
                      <p className="text-sm text-gray-500">
                        {store.distance.toFixed(1)} km away
                      </p>
                    )}
                  </div>
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </div>
      </div>
      
      {/* Store list */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h2 className="text-lg font-medium text-gray-900">
            {stores.length} Store{stores.length !== 1 && 's'} Found
          </h2>
          
          <div className="mt-4">
            {stores.length > 0 ? (
              <ul className="divide-y divide-gray-200">
                {stores.map((store) => (
                  <li key={store.id} className="py-4">
                    <div>
                      <h3 className="text-sm font-medium text-gray-900">
                        {store.name}
                      </h3>
                      <p className="text-sm text-gray-500">{store.address}</p>
                      <p className="text-sm text-gray-500">
                        {store.camera_count} camera{store.camera_count !== 1 && 's'}
                      </p>
                      {store.distance && (
                        <p className="text-sm text-gray-500">
                          {store.distance.toFixed(1)} km away
                        </p>
                      )}
                    </div>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500">No stores found</p>
            )}
          </div>
        </div>
      </div>
      
      {error && (
        <div className="rounded-md bg-red-50 p-4">
          <div className="text-sm text-red-700">{error}</div>
        </div>
      )}
    </div>
  );
}

export default StoreLocator;
