import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { MagnifyingGlassIcon, MapPinIcon } from '@heroicons/react/24/outline';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function ItemFinder() {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedStore, setSelectedStore] = useState(null);
  const [stores, setStores] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [itemLocation, setItemLocation] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  
  const navigate = useNavigate();
  
  // Load stores on component mount
  useEffect(() => {
    const loadStores = async () => {
      try {
        const response = await axios.get(`${API_URL}/stores`);
        setStores(response.data);
        if (response.data.length > 0) {
          setSelectedStore(response.data[0]);
        }
      } catch (error) {
        setError('Failed to load stores');
      }
    };
    
    loadStores();
  }, []);
  
  // Handle search
  const handleSearch = async () => {
    if (!searchQuery.trim()) return;
    
    setIsLoading(true);
    setError(null);
    setSearchResults([]);
    setSelectedItem(null);
    setItemLocation(null);
    
    try {
      const response = await axios.get(`${API_URL}/stores/items/search`, {
        params: {
          q: searchQuery,
          store_id: selectedStore?.id
        }
      });
      
      setSearchResults(response.data);
    } catch (error) {
      setError('Failed to search items');
    }
    
    setIsLoading(false);
  };
  
  // Handle item selection
  const handleItemSelect = async (item) => {
    setSelectedItem(item);
    setIsLoading(true);
    setError(null);
    
    try {
      const response = await axios.get(
        `${API_URL}/stores/items/${item.id}/location`,
        {
          params: { store_id: selectedStore.id }
        }
      );
      
      setItemLocation(response.data);
    } catch (error) {
      setError('Failed to get item location');
    }
    
    setIsLoading(false);
  };
  
  return (
    <div className="space-y-6">
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h2 className="text-lg font-medium text-gray-900">Item Finder</h2>
          
          {/* Store selector */}
          <div className="mt-4">
            <label htmlFor="store" className="block text-sm font-medium text-gray-700">
              Select Store
            </label>
            <select
              id="store"
              value={selectedStore?.id || ''}
              onChange={(e) => {
                const store = stores.find(s => s.id === parseInt(e.target.value));
                setSelectedStore(store);
              }}
              className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
            >
              {stores.map((store) => (
                <option key={store.id} value={store.id}>
                  {store.name} - {store.address}
                </option>
              ))}
            </select>
          </div>
          
          {/* Search bar */}
          <div className="mt-4">
            <div className="flex rounded-md shadow-sm">
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                placeholder="Search for an item..."
                className="flex-1 min-w-0 block w-full px-3 py-2 rounded-none rounded-l-md focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm border-gray-300"
              />
              <button
                onClick={handleSearch}
                disabled={isLoading}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-r-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                {isLoading ? (
                  'Searching...'
                ) : (
                  <>
                    <MagnifyingGlassIcon className="h-5 w-5 mr-2" />
                    Search
                  </>
                )}
              </button>
            </div>
          </div>
          
          {/* Search results */}
          {searchResults.length > 0 && (
            <div className="mt-6">
              <h3 className="text-sm font-medium text-gray-900">Search Results</h3>
              <ul className="mt-3 grid grid-cols-1 gap-4 sm:grid-cols-2">
                {searchResults.map((item) => (
                  <li key={item.id}>
                    <button
                      onClick={() => handleItemSelect(item)}
                      className={`w-full text-left px-4 py-4 border rounded-lg hover:border-indigo-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 ${
                        selectedItem?.id === item.id
                          ? 'border-indigo-500 ring-2 ring-indigo-500'
                          : 'border-gray-300'
                      }`}
                    >
                      <div className="flex items-center justify-between">
                        <div>
                          <h4 className="text-sm font-medium text-gray-900">
                            {item.name}
                          </h4>
                          <p className="text-sm text-gray-500">{item.category}</p>
                        </div>
                        {item.locations.some(loc => loc.store_id === selectedStore?.id) && (
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                            In Stock
                          </span>
                        )}
                      </div>
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )}
          
          {/* Item location */}
          {itemLocation && (
            <div className="mt-6 p-4 border rounded-lg bg-gray-50">
              <div className="flex items-start space-x-4">
                <div className="flex-shrink-0">
                  <MapPinIcon className="h-6 w-6 text-indigo-600" />
                </div>
                <div>
                  <h3 className="text-lg font-medium text-gray-900">
                    {itemLocation.item.name}
                  </h3>
                  <div className="mt-2 text-sm text-gray-500">
                    <p>Store: {itemLocation.location.store_name}</p>
                    <p>Aisle: {itemLocation.location.aisle_number}</p>
                    <p>Shelf: {itemLocation.location.shelf_number}</p>
                    <p>Section: {itemLocation.location.section}</p>
                  </div>
                  
                  {itemLocation.store_layout.image_url && (
                    <div className="mt-4">
                      <img
                        src={itemLocation.store_layout.image_url}
                        alt="Store layout"
                        className="w-full h-auto rounded-lg shadow-sm"
                      />
                      {/* Here you could add an interactive map with the item's position marked */}
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}
          
          {error && (
            <div className="mt-4 rounded-md bg-red-50 p-4">
              <div className="text-sm text-red-700">{error}</div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default ItemFinder;
