import React, { useEffect, useState, useRef, useCallback } from 'react';
import { io } from 'socket.io-client';
import { toast } from 'react-hot-toast';
import useCartStore from '../stores/cartStore';

const API_URL = process.env.REACT_APP_API_URL || 'http://192.168.74.207:5000';

const CameraFeed = ({ cartId, onProductDetected, onConnectionChange }) => {
  const [isConnected, setIsConnected] = useState(false);
  const [lastDetection, setLastDetection] = useState(null);
  const [reconnectAttempts, setReconnectAttempts] = useState(0);
  const socketRef = useRef(null);
  const imageRef = useRef(null);
  const frameBufferRef = useRef('');
  const addItem = useCartStore(state => state.addItem);

  const handleProductDetection = useCallback((data) => {
    setLastDetection(data);
    if (data.class) {
      const product = {
        id: data.class,
        name: data.class,
        price: 0,
        confidence: data.confidence
      };
      addItem(product);
      toast.success(`Detected: ${product.name}`);
    }
  }, [addItem]);

  const setupSocketListeners = useCallback(() => {
    if (!socketRef.current) return;

    socketRef.current.on('connect', () => {
      setIsConnected(true);
      setReconnectAttempts(0);
      if (onConnectionChange) onConnectionChange(true);
      toast.success('Connected to camera feed');
    });

    socketRef.current.on('connect_error', (error) => {
      setIsConnected(false);
      setReconnectAttempts(prev => prev + 1);
      toast.error(`Connection error: ${error.message}`);
    });

    socketRef.current.on('disconnect', () => {
      setIsConnected(false);
      if (onConnectionChange) onConnectionChange(false);
      toast.error('Camera feed disconnected');
    });

    socketRef.current.on('frame_update', (data) => {
      if (!data) return;

      try {
        // Handle chunked images
        if (data.chunk) {
          frameBufferRef.current += data.chunk;
          if (!data.final) return;
          data.image = frameBufferRef.current;
          frameBufferRef.current = '';
        }

        if (imageRef.current && data.image) {
          const img = new Image();
          img.onload = () => {
            imageRef.current.src = img.src;
          };
          img.onerror = () => {
            console.error('Failed to load image');
          };
          img.src = `data:image/jpeg;base64,${data.image}`;
        }

        // Handle detection if present
        if (data.class && onProductDetected) {
          onProductDetected(data);
          handleProductDetection(data);
        }
      } catch (error) {
        console.error('Error processing frame:', error);
      }
    });

    socketRef.current.on('error', (error) => {
      toast.error(`Server error: ${error.message}`);
    });

    // Cleanup function to remove listeners
    return () => {
      if (socketRef.current) {
        socketRef.current.off('connect');
        socketRef.current.off('connect_error');
        socketRef.current.off('disconnect');
        socketRef.current.off('frame_update');
        socketRef.current.off('error');
      }
    };
  }, [onConnectionChange, onProductDetected, handleProductDetection]);

  const connectSocket = useCallback(() => {
    if (socketRef.current) {
      socketRef.current.disconnect();
    }

    socketRef.current = io(API_URL, {
      transports: ['websocket'],
      upgrade: false,
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: Math.min(1000 * Math.pow(2, reconnectAttempts), 10000),
      timeout: 60000,
      query: { cart_id: cartId }
    });

    const cleanup = setupSocketListeners();
    return cleanup;
  }, [cartId, reconnectAttempts, setupSocketListeners]);

  useEffect(() => {
    const cleanup = connectSocket();
    return () => {
      cleanup?.();
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, [connectSocket]);

  return (
    <div className="bg-white p-4 rounded-lg shadow">
      <div className="mb-4">
        <h2 className="text-xl font-semibold mb-2">Camera Feed</h2>
        <div className="flex items-center space-x-2">
          <div
            className={`w-3 h-3 rounded-full ${
              isConnected ? 'bg-green-500' : 'bg-red-500'
            }`}
          />
          <span className="text-sm text-gray-600">
            {isConnected ? 'Connected' : 'Disconnected'}
          </span>
        </div>
      </div>

      <div className="relative aspect-video bg-gray-100 rounded-lg overflow-hidden mb-4">
        <img
          ref={imageRef}
          alt="Camera feed"
          className="w-full h-full object-cover"
        />
        {!isConnected && (
          <div className="absolute inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50 text-white">
            Connecting to camera...
          </div>
        )}
      </div>

      {lastDetection && (
        <div className="p-3 bg-blue-50 rounded-lg">
          <h3 className="font-medium mb-1">Last Detection</h3>
          <div className="text-sm text-gray-600">
            <p>Product: {lastDetection.class}</p>
            <p>
              Confidence: {(lastDetection.confidence * 100).toFixed(1)}%
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default CameraFeed;
