import React, { createContext, useContext, useEffect } from 'react';
import useWebSocketStore from '../stores/websocketStore';
import useAuthStore from '../stores/authStore';

const CartContext = createContext(null);

export const CartProvider = ({ children }) => {
  const { initializeSocket, disconnect, cleanup } = useWebSocketStore();
  const isAuthenticated = useAuthStore(state => state.isAuthenticated);
  const user = useAuthStore(state => state.user);

  // Cleanup WebSocket connection on unmount
  useEffect(() => {
    return () => {
      cleanup();
    };
  }, [cleanup]);

  // Disconnect WebSocket when user logs out
  useEffect(() => {
    if (!isAuthenticated || !user) {
      disconnect();
    }
  }, [isAuthenticated, user, disconnect]);
  const { isConnected, lastFrame, error } = useWebSocketStore();

  const value = {
    connectToCart: (cartId) => {
      if (isAuthenticated && cartId) {
        initializeSocket(cartId);
      }
    },
    disconnectFromCart: disconnect,
    isConnected,
    lastFrame,
    error
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
