import React, { useEffect, useRef, useCallback } from 'react';
import { toast } from 'react-hot-toast';
import useCartStore from '../stores/cartStore';
import useWebSocketStore from '../stores/websocketStore';

const CameraFeed = ({ cartId, onProductDetected, onConnectionChange }) => {
  const imageRef = useRef(null);
  const addItem = useCartStore(state => state.addItem);
  const { 
    initializeSocket, 
    disconnect,
    isConnected,
    error,
    lastFrame
  } = useWebSocketStore();

  const handleProductDetection = useCallback((data) => {
    if (data.class) {
      const product = {
        id: data.class,
        name: data.class,
        price: 0,
        confidence: data.confidence
      };
      addItem(product);
      toast.success(`Detected: ${product.name}`);
    }
  }, [addItem]);

  // Initialize WebSocket connection
  useEffect(() => {
    if (cartId) {
      console.log('Initializing WebSocket connection for cart:', cartId);
      initializeSocket(cartId);
    }
    return () => {
      console.log('Cleaning up WebSocket connection');
      disconnect();
    };
  }, [cartId, initializeSocket, disconnect]);

  // Handle connection status changes
  useEffect(() => {
    if (onConnectionChange) {
      onConnectionChange(isConnected);
    }
  }, [isConnected, onConnectionChange]);

  // Handle errors
  useEffect(() => {
    if (error) {
      toast.error(`Connection error: ${error}`);
    }
  }, [error]);

  // Update image when new frame arrives
  useEffect(() => {
    if (lastFrame && imageRef.current) {
      imageRef.current.src = `data:image/jpeg;base64,${lastFrame}`;
    }
  }, [lastFrame]);

  return (
    <div className="camera-feed">
      <img
        ref={imageRef}
        src="placeholder.jpg"
        alt="Camera Feed"
        style={{ maxWidth: '100%', height: 'auto' }}
      />
      {error && (
        <div className="error-message">
          {error}
        </div>
      )}
      {!isConnected && (
        <div className="connection-status">
          Connecting...
        </div>
      )}
    </div>
  );
};

export default CameraFeed;
