import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  PlusIcon,
  TrashIcon,
  CheckCircleIcon,
  XCircleIcon,
  CurrencyDollarIcon,
  PhotoIcon
} from '@heroicons/react/24/outline';
import MapSelector from '../components/MapSelector';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function AdminDashboard() {
  const [activeTab, setActiveTab] = useState('stores');
  const [stores, setStores] = useState([]);
  const [cameras, setCameras] = useState([]);
  const [models, setModels] = useState([]);
  const [products, setProducts] = useState([]);
  const [systemStatus, setSystemStatus] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // Form states
  const [newStore, setNewStore] = useState({
    name: '',
    address: '',
    location: null
  });
  
  const [newCamera, setNewCamera] = useState({
    store_id: '',
    name: '',
    ip_address: ''
  });
  
  const [newModel, setNewModel] = useState({
    name: '',
    version: '',
    file: null,
    classes: ''
  });

  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    sku: '',
    stock: '',
    image: null
  });
  
  useEffect(() => {
    loadData();
  }, []);
  
  const loadData = async () => {
    try {
      const [storesRes, camerasRes, modelsRes, statusRes, productsRes] = await Promise.all([
        axios.get(`${API_URL}/admin/stores`),
        axios.get(`${API_URL}/admin/cameras`),
        axios.get(`${API_URL}/admin/models`),
        axios.get(`${API_URL}/admin/status`),
        axios.get(`${API_URL}/admin/products`)
      ]);
      
      setStores(storesRes.data);
      setCameras(camerasRes.data);
      setModels(modelsRes.data);
      setSystemStatus(statusRes.data);
      setProducts(productsRes.data);
    } catch (error) {
      setError('Failed to load data');
    }
    setIsLoading(false);
  };
  
  const handleLocationSelect = (location) => {
    setNewStore({ ...newStore, location });
  };
  
  const handleAddStore = async (e) => {
    e.preventDefault();
    
    try {
      // Validate location data
      if (!newStore.location || typeof newStore.location.lat !== 'number' || typeof newStore.location.lng !== 'number') {
        setError('Please select a valid location on the map');
        return;
      }

      // Validate required fields
      if (!newStore.name.trim() || !newStore.address.trim()) {
        setError('Please fill in all required fields');
        return;
      }

      const response = await axios({
        method: 'post',
        url: `${API_URL}/admin/stores`,
        headers: {
          'Content-Type': 'application/json'
        },
        data: {
          subject: "store",
          store_data: {
            name: newStore.name.trim(),
            address: newStore.address.trim(),
            latitude: Number(newStore.location.lat),
            longitude: Number(newStore.location.lng)
          }
        }
      });
      
      if (response.data) {
        setStores([...stores, response.data]);
        setNewStore({ name: '', address: '', location: null });
        setError(null); // Clear any previous errors
      }
    } catch (error) {
      console.error('Store creation error:', error.response?.data || error.message);
      const errorMessage = error.response?.data?.msg || error.response?.data?.message || 'Failed to add store. Please check all fields and try again.';
      setError(errorMessage);
    }
  };
  
  const handleAddCamera = async (e) => {
    e.preventDefault();
    
    try {
      const response = await axios.post(`${API_URL}/admin/cameras`, newCamera);
      setCameras([...cameras, response.data]);
      setNewCamera({ store_id: '', name: '', ip_address: '' });
    } catch (error) {
      setError('Failed to add camera');
    }
  };
  
  const handleAddModel = async (e) => {
    e.preventDefault();
    
    try {
      const formData = new FormData();
      formData.append('name', newModel.name);
      formData.append('version', newModel.version);
      formData.append('model', newModel.file);
      formData.append('classes', newModel.classes);
      
      const response = await axios.post(`${API_URL}/admin/models`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      
      setModels([...models, response.data]);
      setNewModel({ name: '', version: '', file: null, classes: '' });
    } catch (error) {
      setError('Failed to add model');
    }
  };
  
  const handleActivateModel = async (modelId) => {
    try {
      await axios.post(`${API_URL}/admin/models/${modelId}/activate`);
      loadData();  // Reload all data to get updated status
    } catch (error) {
      setError('Failed to activate model');
    }
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    
    try {
      const formData = new FormData();
      formData.append('name', newProduct.name.trim());
      formData.append('description', newProduct.description.trim());
      formData.append('price', parseFloat(newProduct.price));
      formData.append('category', newProduct.category.trim());
      formData.append('sku', newProduct.sku.trim());
      formData.append('stock', parseInt(newProduct.stock));
      if (newProduct.image) {
        formData.append('image', newProduct.image);
      }

      const response = await axios.post(`${API_URL}/admin/products`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data) {
        setProducts([...products, response.data]);
        setNewProduct({
          name: '',
          description: '',
          price: '',
          category: '',
          sku: '',
          stock: '',
          image: null
        });
        setError(null);
      }
    } catch (error) {
      console.error('Product creation error:', error.response?.data || error.message);
      const errorMessage = error.response?.data?.msg || error.response?.data?.message || 'Failed to add product. Please check all fields and try again.';
      setError(errorMessage);
    }
  };
  
  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="text-lg text-gray-600">Loading...</div>
      </div>
    );
  }
  
  return (
    <div className="space-y-6">
      {/* System Status */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h2 className="text-lg font-medium text-gray-900">System Status</h2>
          
          {systemStatus && (
            <div className="mt-4 grid grid-cols-1 gap-4 sm:grid-cols-3">
              <div className="bg-gray-50 p-4 rounded-lg">
                <h3 className="text-sm font-medium text-gray-500">Stores</h3>
                <p className="mt-2 text-3xl font-semibold text-gray-900">
                  {systemStatus.stores.total}
                </p>
              </div>
              
              <div className="bg-gray-50 p-4 rounded-lg">
                <h3 className="text-sm font-medium text-gray-500">Cameras</h3>
                <p className="mt-2 text-3xl font-semibold text-gray-900">
                  {systemStatus.cameras.active}/{systemStatus.cameras.total}
                </p>
              </div>
              
              <div className="bg-gray-50 p-4 rounded-lg">
                <h3 className="text-sm font-medium text-gray-500">Active Model</h3>
                <p className="mt-2 text-lg font-medium text-gray-900">
                  {systemStatus.model.name || 'None'}
                  {systemStatus.model.version && ` (v${systemStatus.model.version})`}
                </p>
              </div>
            </div>
          )}
        </div>
      </div>
      
      {/* Tabs */}
      <div className="border-b border-gray-200">
        <nav className="-mb-px flex space-x-8">
          {['stores', 'cameras', 'models', 'products'].map((tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`${
                activeTab === tab
                  ? 'border-indigo-500 text-indigo-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm capitalize`}
            >
              {tab}
            </button>
          ))}
        </nav>
      </div>
      
      {/* Tab content */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          {/* Stores tab */}
          {activeTab === 'stores' && (
            <div>
              <form onSubmit={handleAddStore} className="space-y-4 mb-8">
                <h3 className="text-lg font-medium text-gray-900">Add Store</h3>
                
                <div className="grid grid-cols-1 gap-4">
                  <input
                    type="text"
                    value={newStore.name}
                    onChange={(e) => setNewStore({ ...newStore, name: e.target.value })}
                    placeholder="Store name"
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  />
                  <input
                    type="text"
                    value={newStore.address}
                    onChange={(e) => setNewStore({ ...newStore, address: e.target.value })}
                    placeholder="Address"
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  />
                  
                  <div className="border border-gray-300 rounded-md p-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Select Store Location
                    </label>
                    <MapSelector
                      onLocationSelect={handleLocationSelect}
                      initialLocation={newStore.location}
                    />
                    {!newStore.location && (
                      <p className="mt-2 text-sm text-red-600">
                        Please select a location on the map
                      </p>
                    )}
                  </div>
                </div>
                
                <button
                  type="submit"
                  disabled={!newStore.location}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 disabled:bg-gray-400 disabled:cursor-not-allowed"
                >
                  <PlusIcon className="h-5 w-5 mr-2" />
                  Add Store
                </button>
              </form>
              
              <ul className="divide-y divide-gray-200">
                {stores.map((store) => (
                  <li key={store.id} className="py-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <h4 className="text-sm font-medium text-gray-900">
                          {store.name}
                        </h4>
                        <p className="text-sm text-gray-500">{store.address}</p>
                        <p className="text-sm text-gray-500">
                          {store.camera_count} camera{store.camera_count !== 1 && 's'}
                        </p>
                      </div>
                      <div className="flex items-center">
                        {store.is_active ? (
                          <CheckCircleIcon className="h-5 w-5 text-green-500" />
                        ) : (
                          <XCircleIcon className="h-5 w-5 text-red-500" />
                        )}
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          )}
          
          {/* Cameras tab */}
          {activeTab === 'cameras' && (
            <div>
              <form onSubmit={handleAddCamera} className="space-y-4 mb-8">
                <h3 className="text-lg font-medium text-gray-900">Add Camera</h3>
                
                <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                  <select
                    value={newCamera.store_id}
                    onChange={(e) => setNewCamera({ ...newCamera, store_id: e.target.value })}
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  >
                    <option value="">Select store</option>
                    {stores.map((store) => (
                      <option key={store.id} value={store.id}>
                        {store.name}
                      </option>
                    ))}
                  </select>
                  
                  <input
                    type="text"
                    value={newCamera.name}
                    onChange={(e) => setNewCamera({ ...newCamera, name: e.target.value })}
                    placeholder="Camera name"
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  />
                  
                  <input
                    type="text"
                    value={newCamera.ip_address}
                    onChange={(e) => setNewCamera({ ...newCamera, ip_address: e.target.value })}
                    placeholder="IP Address"
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  />
                </div>
                
                <button
                  type="submit"
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700"
                >
                  <PlusIcon className="h-5 w-5 mr-2" />
                  Add Camera
                </button>
              </form>
              
              <ul className="divide-y divide-gray-200">
                {cameras.map((camera) => (
                  <li key={camera.id} className="py-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <h4 className="text-sm font-medium text-gray-900">
                          {camera.name}
                        </h4>
                        <p className="text-sm text-gray-500">{camera.ip_address}</p>
                        <p className="text-sm text-gray-500">
                          Last seen: {camera.last_seen ? new Date(camera.last_seen).toLocaleString() : 'Never'}
                        </p>
                      </div>
                      <div className="flex items-center">
                        {camera.status === 'online' ? (
                          <CheckCircleIcon className="h-5 w-5 text-green-500" />
                        ) : (
                          <XCircleIcon className="h-5 w-5 text-red-500" />
                        )}
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          )}
          
          {/* Models tab */}
          {activeTab === 'models' && (
            <div>
              <form onSubmit={handleAddModel} className="space-y-4 mb-8">
                <h3 className="text-lg font-medium text-gray-900">Add Model</h3>
                
                <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                  <input
                    type="text"
                    value={newModel.name}
                    onChange={(e) => setNewModel({ ...newModel, name: e.target.value })}
                    placeholder="Model name"
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  />
                  
                  <input
                    type="text"
                    value={newModel.version}
                    onChange={(e) => setNewModel({ ...newModel, version: e.target.value })}
                    placeholder="Version"
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  />
                  
                  <input
                    type="file"
                    accept=".h5"
                    onChange={(e) => setNewModel({ ...newModel, file: e.target.files[0] })}
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  />
                  
                  <textarea
                    value={newModel.classes}
                    onChange={(e) => setNewModel({ ...newModel, classes: e.target.value })}
                    placeholder="Class labels (JSON array)"
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  />
                </div>
                
                <button
                  type="submit"
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700"
                >
                  <PlusIcon className="h-5 w-5 mr-2" />
                  Add Model
                </button>
              </form>
              
              <ul className="divide-y divide-gray-200">
                {models.map((model) => (
                  <li key={model.id} className="py-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <h4 className="text-sm font-medium text-gray-900">
                          {model.name} (v{model.version})
                        </h4>
                        <p className="text-sm text-gray-500">
                          Created: {new Date(model.created_at).toLocaleString()}
                        </p>
                      </div>
                      <div className="flex items-center space-x-4">
                        {model.is_active ? (
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                            Active
                          </span>
                        ) : (
                          <button
                            onClick={() => handleActivateModel(model.id)}
                            className="inline-flex items-center px-3 py-1 border border-transparent text-xs font-medium rounded text-indigo-700 bg-indigo-100 hover:bg-indigo-200"
                          >
                            Activate
                          </button>
                        )}
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          )}

          {/* Products tab */}
          {activeTab === 'products' && (
            <div>
              <form onSubmit={handleAddProduct} className="space-y-4 mb-8">
                <h3 className="text-lg font-medium text-gray-900">Add Product</h3>
                
                <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                  <input
                    type="text"
                    value={newProduct.name}
                    onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
                    placeholder="Product name"
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  />
                  
                  <input
                    type="text"
                    value={newProduct.category}
                    onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })}
                    placeholder="Category"
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  />
                  
                  <textarea
                    value={newProduct.description}
                    onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
                    placeholder="Product description"
                    rows={3}
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:col-span-2"
                    required
                  />
                  
                  <div className="flex items-center space-x-2">
                    <CurrencyDollarIcon className="h-5 w-5 text-gray-400" />
                    <input
                      type="number"
                      step="0.01"
                      min="0"
                      value={newProduct.price}
                      onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
                      placeholder="Price"
                      className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                      required
                    />
                  </div>
                  
                  <input
                    type="text"
                    value={newProduct.sku}
                    onChange={(e) => setNewProduct({ ...newProduct, sku: e.target.value })}
                    placeholder="SKU"
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  />
                  
                  <input
                    type="number"
                    min="0"
                    value={newProduct.stock}
                    onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })}
                    placeholder="Stock quantity"
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    required
                  />
                  
                  <div className="sm:col-span-2">
                    <label className="block text-sm font-medium text-gray-700">Product Image</label>
                    <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md">
                      <div className="space-y-1 text-center">
                        <PhotoIcon className="mx-auto h-12 w-12 text-gray-400" />
                        <div className="flex text-sm text-gray-600">
                          <label htmlFor="product-image" className="relative cursor-pointer bg-white rounded-md font-medium text-indigo-600 hover:text-indigo-500 focus-within:outline-none focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-indigo-500">
                            <span>Upload a file</span>
                            <input
                              id="product-image"
                              type="file"
                              accept="image/*"
                              onChange={(e) => setNewProduct({ ...newProduct, image: e.target.files[0] })}
                              className="sr-only"
                            />
                          </label>
                          <p className="pl-1">or drag and drop</p>
                        </div>
                        <p className="text-xs text-gray-500">PNG, JPG, GIF up to 10MB</p>
                      </div>
                    </div>
                  </div>
                </div>
                
                <button
                  type="submit"
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700"
                >
                  <PlusIcon className="h-5 w-5 mr-2" />
                  Add Product
                </button>
              </form>
              
              <ul className="divide-y divide-gray-200">
                {products.map((product) => (
                  <li key={product.id} className="py-4">
                    <div className="flex items-center space-x-4">
                      {product.image_url && (
                        <img
                          src={product.image_url}
                          alt={product.name}
                          className="h-16 w-16 object-cover rounded-md"
                        />
                      )}
                      <div>
                        <h4 className="text-sm font-medium text-gray-900">
                          {product.name}
                        </h4>
                        <p className="text-sm text-gray-500">{product.description}</p>
                        <div className="mt-1 flex items-center space-x-4 text-sm">
                          <span className="text-green-600 font-medium">${product.price}</span>
                          <span className="text-gray-500">SKU: {product.sku}</span>
                          <span className="text-gray-500">Stock: {product.stock}</span>
                        </div>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
      
      {error && (
        <div className="rounded-md bg-red-50 p-4">
          <div className="text-sm text-red-700">{error}</div>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
